import * as types from "../constants/ActionStyle";

var initialState = [];

const myReducer = (State = initialState, action) => {
    switch(action.type){
      case types.FETCH_PRODUCT:
        State = action.product;
        return [...State];
      case types.ADD_PRODUCT:
        State.push(action.product);
        return [...State]
      default: 
        return [...State];
    }
}

export default myReducer;
