import React, { Component } from "react";
import Products from "../component/products/Products";
import ProductItem from "../component/productItem/ProductItem";
import { connect } from "react-redux";
import callApi from "../utils/callApi";
import { findIndex } from "lodash";

class ProductsPage extends Component {
  constructor(props) {
    super(props);
    this.state = {
      product: [],
    };
  }

  componentDidMount() {
    callApi("/Product",'GET', null).then(res => {
      this.setState({
        product: res.data,
      });
    });
  }

  render() {
    var { product } = this.state;
    return (
      <div className="row">
        {/* Products */}
        <Products>{this.showProductItem(product)}</Products>
      </div>
    );
  }

  showProductItem = (products) => {
    var result = null;
    if (products) {
      result = products.map((item, index) => {
        return <ProductItem key={index} product={item} onDelete={this.onDelete}/>;
      });
    }
    return result;
  };

  onDelete = id => {
    var {product} = this.state;
    console.log(product);
    callApi(`/Product/${id}`,'DELETE', null).then(res => {
      var index = findIndex(product.product, (item) => {
        return item.id === id;
      })
      product.splice(index, 1);
      this.setState({
        product
      })
    });
  }
}

var mapStateToProps = (state) => {
  return {
    data: state.product,
  };
};

export default connect(mapStateToProps, null)(ProductsPage);
