import React, { Component } from "react";

import { connect } from "react-redux";
import * as actions from "../../actions/index";

class SearchList extends Component {
  constructor(props) {
    super(props);
    this.state = {
      idDi: null,
      idDen: null,
      dateDi: null,
      dateDen: null,
      CodeHang: null
    };
    this.handleChange = this.handleChange.bind(this);
  }

  componentDidMount() {
    this.props.fetchFlights();
  }
  handleChange(event) {

    event.preventDefault();
    var target = event.target;
    var name = target.name;
    var value = target.type === "checkbox" ? target.checked : target.value;
    this.setState({
      [name]: value,
    });
  }
  render() {
    var flights = this.props.data;
    console.log(this.getDate());
    return (
      <div>
        <div>
          <div className="bg-inner small-section pb-0">
            <div className="container">
              <div className="flight-search">
                    <div className="flight-search-detail">
                      <form className="row m-0">
                        <div className="col-lg-2">
                          <div className="form-group">
                            <label>Từ</label>
                            <select 
                              name="idDi" 
                              className="form-control open-select"
                              value={this.state.idDi} 
                              onChange={this.handleChange}
                              >
                              {this.showSanBayDi(flights)}
                            </select>
                            <img
                              src="../assets/images/icon/from.png"
                              className="img-fluid blur-up lazyload"
                              alt
                            />
                          </div>
                        </div>
                        <div className="col-lg-2">
                          <div className="form-group">
                            <label>Đến</label>
                            <select 
                            name="idDen" 
                            className="form-control open-select"
                            value={this.state.idDen} 
                            onChange={this.handleChange}
                            >
                                {this.showSanBayDen(flights, this.state.id)}
                            </select>
                            <img
                              src="../assets/images/icon/location.png"
                              className="img-fluid blur-up lazyload"
                              alt
                            />
                          </div>
                        </div>
                        <div className="col-lg-2">
                          <div className="form-group">
                            <label>Ngày khởi hành</label>
                            <input
                              name="dateDi"
                              placeholder="Depart Date"
                              id="datepicker"
                              className="form-control open-select"
                              type="date" min={this.getDate()} max="2030-01-01"
                              value={this.state.dateDi} 
                              onChange={this.handleChange}
                            />
                          </div>
                        </div>
                        <div className="col-lg-2">
                          <div className="form-group">
                            <label>Ngày trở về</label>
                            <input 
                              name="dateDen"
                              className="form-control open-select"
                              type="date" 
                              min={this.state.dateDi} max="2022-01-01"
                              placeholder="Return Date" 
                              id="datepicker1"
                              value={this.state.dateDen} 
                              onChange={this.handleChange}
                              />
                          </div>
                        </div>
                        <div className="col-lg-2">
                          <div className="form-group">
                            <label>nhà lữ hành &amp; hạng</label>
                            <select  
                              name="CodeHang" 
                              className="form-control open-select" 
                              value={this.state.CodeHang} 
                              onChange={this.handleChange}
                            >
                              <option value="ALL">ALL</option>
                              <option value="VN">Vietnam Airlines</option>
                              <option value="VJ">VietJet Air</option>
                              <option value="BB">Bamboo Airways</option>
                            </select>
                            <img
                              src="../assets/images/icon/user.png"
                              className="img-fluid blur-up lazyload"
                              alt
                            />
                          </div>
                        </div>
                        <div className="col-lg-2">
                          <div className="search-btn">
                            <a href="#" className="btn btn-solid color1">
                              tìm kiếm
                            </a>
                          </div>
                        </div>
                      </form>
                    </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }

  getDate = () => {
    var today = new Date();
    var dd = String(today.getDate()).padStart(2, '0');
    var mm = String(today.getMonth() + 1).padStart(2, '0'); //January is 0!
    var yyyy = today.getFullYear();

    return today = yyyy + '-' + mm + '-' + dd;
  }

  showSanBayDi = (flights) => {
    var result = null;
    if (flights) {
      result = flights.map((item, index) => {
        return <option key={index} value={item.id}>{item.TenSanBay}</option>;
      });
    }
    return result;
  };

  showSanBayDen = (flights, id) => {
    var result = null;
    if (flights) {
      result = flights.map((item, index) => {
        if(item.id != id)
        {
          return <option key={index} value={item.id}>{item.TenSanBay}</option>;
        }
      });
    }
    return result;
  };
}

var mapStateToProps = (state) => {
  return {
    data: state.flight
  };
};

var mapDispatchToProps = (dispatch, props) => {
  return {
    fetchFlights :() =>  {
      dispatch(actions.actFetchFlightCallApi());
    }
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(SearchList);
