import Axios from "axios";
import {API_URL} from "../constants/ActionAPI";
export default function (endpoind, method = 'GET', data) {
    console.log(method);
    Axios({
        method: {method},
        url: `${API_URL}/${endpoind}`,
        data: data
      }).then(res => {
        return res;
      }).catch(err => {
          console.log(err);
        return null;
      });
}
