import React, { Component } from "react";
import callApi from "../utils/callApi";
import { history } from "react-router-dom";

class ProductActionPage extends Component {

  constructor(props) {
    super(props);
    this.state = {
      txtName: '',
      txtPrice: '',
      chkbStatus: '',
    }
  }

  onChange = (event) => {
    event.preventDefault();
    var target = event.target;
    var name  = target.name;
    var value = target.type === 'checkbox' ? target.checked : target.value;
    this.setState({
      [name] : value
    })
    console.log(value);
  }

  onSave = (event) => {
    event.preventDefault();
    var {txtName, txtPrice, chkbStatus} = this.state;
    callApi("/Product/",'POST', {
      name: txtName,
      price: txtPrice,
      status: chkbStatus
    }).then(res => {
      console.log(res);
    });
  }


  render() {
    return (
      <div className="row">
        <div className="col-xs-4 col-sm-4 col-md-4 col-lg-4">
          <form onSubmit={this.onSave}>
            <legend>Add product</legend>

            <div className="form-group">
              <label>Name: </label>
              <input 
                  type="text" className="form-control" name="txtName" onChange={this.onChange}/>
            </div>

            <div className="form-group">
              <label>Price: </label>
              <input type="number" className="form-control" name="txtPrice" onChange={this.onChange}/>
            </div>

            <div className="form-group">
              <label>Status: </label>
              <div className="checkbox">
                <label>
                  <input type="checkbox" name="chkbStatus" onChange={this.onChange}/>
                  Stocking
                </label>
              </div>
            </div>

            <button type="submit" className="btn btn-primary">
              Submit
            </button>
          </form>
        </div>
      </div>
    );
  }
}

export default ProductActionPage;
