import * as types from "../constants/ActionStyle";


const myReducer = (State = null, action) => {
    switch(action.type){
      case types.REGISTER_USER:
        State = action.infoUser;
        return State;
      case types.LOGIN:
        State = action.infoUser;
        return State;
      case types.LOGOUT:
        State = null;
        return State;
      default: 
        return State;
    }
}

export default myReducer;
